require 'rubygems'
require 'active_record'

ActiveRecord::Base.establish_connection(
  :adapter => 'sqlite3',
  :database =>  'constitution.sqlite3.db'
)

ActiveRecord::Base.connection.create_table(:articles) do |t|
  t.integer :section_id
  t.text :content
  t.boolean :add_bullets, default: true
end

ActiveRecord::Base.connection.create_table(:sections) do |t|
  t.string :title
end

class Section < ActiveRecord::Base
  has_many :articles
end

class Article < ActiveRecord::Base
  belongs_to :section
end

Section.delete_all
Article.delete_all

Section.create id: 1, title: "ЗАГАЛЬНІ ЗАСАДИ"
